hitting25 &lt;- hitting |&gt;
  filter(Team %in% top25)

ggplot() + 
  geom_dumbbell(
    data=hitting25, 
    aes(y=Team, x=Pct., xend=`Opp Pct`),
    size = 1,
    color = "grey",
    colour_x = "green",
    colour_xend = "red") + 
  labs(title = "Nebraska Hits Well and Keeps Opponents from Hitting Well", 
    subtitle =" &lt;span style = 'color:green;'&gt;Hitting Percentage&lt;/span&gt; and &lt;span style = 'color:red;'&gt;Opponent Hitting Percentage&lt;/span&gt;", 
    y="", 
    x="Opponent Hitting Percentage (Red) and Hitting Percentage (Green)",
    caption="Source: NCAA.com | By Hannah Preissler") +
  theme_minimal() +
  theme(
    plot.title = element_text(size = 15, face = "bold"),
    axis.title = element_text(size = 8), 
    plot.subtitle = element_text(size=10), 
    panel.grid.minor = element_blank(),
    plot.title.position = "plot"
    )







